    def getData(self):
        date = self.comboBox_4.currentText() + "-" + self.comboBox.currentText() + "-" + self.comboBox_5.currentText()

        response = requests.get(url + "/modelBitcoin?fecha=" + date)
        info = response.json()

        self.lineEdit.setText(str(info[date+"T00:00:00.000Z"]["lower Close"]))
        self.lineEdit_2.setText(str(info[date+"T00:00:00.000Z"]["upper Close"]))
        

